mapper.select:
SELECT c_id, c_pwd
FROM client_tbl
WHERE c_id = #{c_id}
AND c_pwd = #{c_pwd}

SELECT COUNT(*) FROM client_tbl
WHERE c_id = #{c_id}
AND c_pwd = #{c_pwd}

SELECT FROM client_tbl
WHERE c_id = #{c_id}
	AND c_pwd = #{c_pwd}

SELECT c_pwd
FROM client_tbl
WHERE c_id = #{c_id} AND c_pwd = #{c_pwd}

mapper.delete:
UPDATE client_tbl
SET c_disabled = '0'
WHERE c_id = #{c_id}
AND c_pwd = #{c_pwd}

mapper.update:
UPDATE reservation_tbl
SET r_status = '2'
WHERE r_num = #{r_num}
